    mean_Min = "Mean",
    sd_Min = "SD", 
    mean_Mean = "Mean", 
    sd_Mean = "SD", 
    max_Max = "Max",
    mean_Max = "Mean",
    sd_Max= "SD"
  ) %>%
  tab_spanner(
    label = "Monthly Minimums",
    columns = c(min_Min, mean_Min, sd_Min)
  ) %>%
  tab_spanner(
    label = "Monthly Means",
    columns = c(mean_Mean, sd_Mean)
  ) %>%
  tab_spanner(
    label = "Monthly Maximums",
    columns = c(mean_Max, sd_Max, max_Max)
)
